import axios from 'axios';
import OrgChart from '@/components/OrgChart.vue'; // OrgChart 컴포넌트 임포트

export default {
  name: 'MainPage',
  components: {
    OrgChart // components에서 OrgChart 컴포넌트 등록
  },
  data() {
    return {
      points: 0,
      // 초기 포인트 값
      selectedTab: 'members',
      // 초기 탭 선택
      showOrgChart: false,
      // 추천인 조직도 표시 여부
      products: [] // 상품 목록 데이터
    };
  },
  created() {
    // 로그인 후 사용자 포인트 값을 가져옵니다
    this.fetchUserPoints();
    // 상품 목록을 가져옵니다
    this.fetchProducts();
  },
  methods: {
    // 서버에서 포인트를 받아오는 API 호출
    async fetchUserPoints() {
      try {
        const response = await axios.get('http://localhost:3000/user/points', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        this.points = response.data.points;
      } catch (error) {
        console.error('포인트를 가져오는 데 실패했습니다', error);
      }
    },
    // 서버에서 상품 목록을 가져오는 API 호출
    async fetchProducts() {
      try {
        const response = await axios.get('http://localhost:3000/products'); // 상품 목록 API 호출
        this.products = response.data; // 이미 JSON 데이터로 반환되므로 response.data를 사용합니다.
      } catch (error) {
        console.error('상품 목록을 가져오는 데 실패했습니다', error);
      }
    },
    // 탭 선택 시, 해당 탭을 활성화하는 메서드
    selectTab(tab) {
      this.selectedTab = tab;
      this.showOrgChart = false; // 탭 전환 시 조직도 초기화
    }
  }
};
