//ProductDetail.vue

<template>
  <div>
    <h1>{{ product.name }}</h1>
    <img v-if="product.image" :src="product.image" alt="상품 이미지" />
    <p>가격: {{ product.price }}원</p>

    <!-- 구매 갯수 선택 -->
    <div>
      <label for="quantity">구매 갯수:</label>
      <input id="quantity" v-model.number="quantity" type="number" min="1" />
    </div>

    <!-- 구매 버튼 -->
    <button @click="buyProduct">구매하기</button>

    <!-- 구매 성공 메시지 -->
    <p v-if="successMessage">{{ successMessage }}</p>
  </div>
</template>

<script>
import axios from 'axios';

export default {
  name: 'ProductDetail',
  data() {
    return {
      product: {}, // 상품 정보
      quantity: 1, // 구매 갯수
      successMessage: '', // 구매 성공 메시지
    };
  },
  created() {
    // 상품 ID를 기반으로 상세 정보 가져오기
    const productId = this.$route.params.id;
    this.fetchProduct(productId);
  },
  methods: {
    async fetchProduct(productId) {
      try {
        const response = await axios.get(`http://localhost:3000/products/${productId}`);
        this.product = response.data;
      } catch (error) {
        console.error('상품 정보를 가져오는 데 실패했습니다:', error);
      }
    },
    async buyProduct() {
      try {
        const response = await axios.post(
          `http://localhost:3000/purchase`,
          {
            productId: this.product.id,
            quantity: this.quantity,
          },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          }
        );

        this.successMessage = '구매가 완료되었습니다!';
        console.log(response.data); // 구매 결과 출력 (포인트 적립 내역 포함)
      } catch (error) {
        console.error('구매를 처리하는 데 실패했습니다:', error);
      }
    },
  },
};
</script>

<style scoped>
/* 원하는 스타일 추가 */
</style>
